            </div>
            <h1>{{ $periodLabel }} Insights</h1>
            <div class="period">{{ $insights['period']['label'] }}</div>
        </div>

        <div class="content">
            <!-- Summary Cards -->
            <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom: 25px;">
                <tr>
                    <td width="33%" style="padding: 5px;">
                        <div style="text-align: center; background: #f0fdf4; padding: 20px 10px; border-radius: 10px;">
                            <div style="font-size: 20px; margin-bottom: 5px;">&#x2B06;</div>
                            <div style="font-size: 20px; font-weight: 700; color: #10b981;">{{ number_format($insights['income'], 2) }}</div>
                            <div style="font-size: 11px; color: #6b7280; text-transform: uppercase; margin-top: 4px;">Income</div>
                        </div>
                    </td>
                    <td width="33%" style="padding: 5px;">
                        <div style="text-align: center; background: #fef2f2; padding: 20px 10px; border-radius: 10px;">
                            <div style="font-size: 20px; margin-bottom: 5px;">&#x2B07;</div>
                            <div style="font-size: 20px; font-weight: 700; color: #dc2626;">{{ number_format($insights['expenses'], 2) }}</div>
                            <div style="font-size: 11px; color: #6b7280; text-transform: uppercase; margin-top: 4px;">Expenses</div>
                        </div>
                    </td>
                    <td width="33%" style="padding: 5px;">
                        <div style="text-align: center; background: #e6f2ec; padding: 20px 10px; border-radius: 10px;">
                            <div style="font-size: 20px; margin-bottom: 5px;">&#x1F4B0;</div>
                            <div style="font-size: 20px; font-weight: 700; color: #047844;">{{ number_format($insights['net'], 2) }}</div>
                            <div style="font-size: 11px; color: #6b7280; text-transform: uppercase; margin-top: 4px;">Net</div>
                        </div>
                    </td>
                </tr>
            </table>

            <!-- Stats Highlight -->
            <div class="section">
                <div class="section-title">
                    <span class="icon">&#x1F4CA;</span> Key Metrics
                </div>
                <div class="highlight-box">
                    <div class="stat-row">
                        <span class="stat-label">Savings Rate</span>
                        <span class="stat-value" style="color: #047844;">{{ $insights['savings_rate'] }}%</span>
                    </div>
                    <div class="stat-row">
                        <span class="stat-label">Total Transactions</span>
                        <span class="stat-value">{{ $insights['transaction_count'] }}</span>
                    </div>
                    <div class="stat-row">
                        <span class="stat-label">vs Previous Period</span>
                        <span class="stat-value {{ $insights['expense_change_percent'] > 0 ? 'stat-positive' : 'stat-negative' }}">
